# Intuition
Comparison can be optimized using Rabin Karp other than that its simple dp problem

# Approach
Iterate through the starting point while keeping the ending point to be compared with and use dp to check the maximum value.

# Complexity

- Space complexity:
    O(N^2)

# Code
```
class Solution {
public:
    const long factor = 26;
    const long MOD = 1e9+7;
    bool check(string &text, int l, int r, int end, vector<long>&val, vector<long> &calc)
    {
        if(((val[r] - (l==0?0:val[l-1])+ MOD ) * calc[end - (r-l)]) % MOD == ((val[end] - val[end-(r-l)-1] + MOD) * calc[l])%MOD)
            return true;
        return false;   
    }
    int find(vector<vector<int>> &dp, int idx, int end, vector<long> &val, string &text, vector<long> &calc)
    {
        if(idx==end)
            return 1;
        if(idx>=text.length()/2)
            return 0;
        if(dp[idx][end]!=-1)
            return dp[idx][end];
        for(int i=idx;i<text.size()/2;i++)
        {
            if(check(text, idx, i, end, val, calc))
                dp[idx][end]=max(dp[idx][end], find(dp, i+1, end-(i-idx)-1, val, text, calc)+2);
        }
        return dp[idx][end]==-1?1:dp[idx][end];
    }
    int longestDecomposition(string text) {
        vector<long> calc(text.size()+1), val(text.size()+1);
        for(int i=0;i<calc.size();i++)
        {
            if(i==0)
                calc[i]=1;
            else
                calc[i]=(calc[i-1]*factor)%MOD;
        }
        int val_of_b=0;
        for(int i=0;i<text.length();i++)
        {
            val_of_b = (val_of_b + ((text[i]-'a'+1) * calc[i])%MOD)%MOD;
            val[i]=val_of_b;
        }
        vector<vector<int>>dp(text.size(), vector<int>(text.size(), -1));
        return find(dp, 0, text.size()-1, val, text, calc);
    }
};
