class Solution {
public:
    int compute_lps(string& s,int st,int en){

        //  case 1
        if(s[st]==s[en]){
           return 1;
        }

        // case 2 : lps
        int n = en-st+1;
        vector<int>lps(n,st);

        int len = st;
        int i = st+1;

        while(i<=en){
            if(s[i] == s[len]){
                lps[i-st] = len+1;
                i++;
                len++;
            }
            else if(len == st){
                lps[i-st] = st;
                i++;
            }
            else{
                len = lps[len-1-st];
            }
        }
        return len-st;
    }

    int solve(string& text,int st,int en){
        // base case
        if(st>en) return 0;
        if(st == en) return 1;

        // compute lps
        int lps_len = compute_lps(text,st,en);
        if(lps_len == 0) return 1;
        return 2 + solve(text,st+lps_len,en-lps_len);
    }

    int longestDecomposition(string text) {
        return solve(text,0,text.size()-1);
    }
};
