# Code
```
template<typename T>
class RABIN_KARP_HASH {
    long long p{}, mod{};
    std::vector<long long> pref_hash;
    std::vector<int> p_pow;

public:
    RABIN_KARP_HASH (T &s, int p, int mod) {
        this->p = p;
        this->mod = mod;
        init(s);
    }

    void init(T& s) {
        int n = s.size();
        pref_hash.resize(n);
        pref_hash[0] = s[0];
        for (int i = 1; i < n; i++) {
            pref_hash[i] = (pref_hash[i-1] * p + s[i]) % mod;
        }
        p_pow.resize(n);
        p_pow[0] = 1;
        for (int i = 1; i < n; i++) {
            p_pow[i] = (p_pow[i-1] * 1LL * p) % mod;
        }
    }

    long long get_hash(int i, int j) {
        if (i == 0) return pref_hash[j];
        long long ans = pref_hash[j] - (pref_hash[i-1] * p_pow[j-i+1]) % mod;
        if (ans < 0) ans += mod;
        return ans;
    }

};

class Solution {

public:
    int longestDecomposition(string& text) {
        RABIN_KARP_HASH<string> h1(text, 31, 1e9+7), h2(text, 67, 1e9+9);
        int n = text.size();
        int dp[n][n];
        memset(dp, -1, sizeof dp);
        function<int(int, int)> rec = [&] (int x, int y) {
            if (x > y) return 0;
            if (x == y) return 1;
            if (dp[x][y] != -1) return dp[x][y];
            int len = y-x+1;
            int ans = 1;
            for (int i = 0; i < (len>>1); i++) {
                if (h1.get_hash(x, x+i) == h1.get_hash(y-i, y) && h2.get_hash(x, x+i) == h2.get_hash(y-i, y)) {
                    ans = max(ans, rec(x+i+1, y-i-1)+2);
                }
            }
            return dp[x][y] = ans;
        };
        return rec(0, n-1);
    }
};


```

