class Solution {
public:
    int dp[1001][1001];
    int solve(string_view & s,int i,int j)
    {
        if(i == j) return 1;
        if(dp[i][j]!=-1) return dp[i][j];
        int res = 1;
        for(int k = 1;k<=s.length()/2;k++)
        {
            if(s.substr(0,k) == s.substr(s.length()-k))
            {
                string_view t = s.substr(k,s.length()-2*k);
                if(t.length() ==  0)
                {
                    res = max(res,solve(t,i,j-k)+1);
                }else
                {
                    res = max(res,solve(t,i,j-k)+2);
                }
            }
        }
        return dp[i][j] = res;
    }
    int longestDecomposition(string text) {
        memset(dp,-1,sizeof(dp));
        string_view ss = string_view(text);
        return solve(ss,0,text.length()-1);
    }
};
