class Solution {
public:
    int longestDecomposition(string text) {
        int start = 0, end = text.length()-1;
        int count = 0, endL = end+1;
        while (start < end) {
            if (text[end] == text[start]) {
                int i = start, j = end;
                while (j < endL && (text[i] == text[j])) {
                    i++; j++;
                }
                if (std::abs(start - i)==std::abs(end-endL)) {
                    endL = end;
                    end--;
                    start = i;
                    count+=2;
                } else {
                    end--;
                }
            } else {
                end--;
            }
        }
        if (start == end) count++;
        return count;
    }
};
