func longestDecomposition(text string) int {
	// used for right pointer
	n := len(text)-1
	// last is used to keep track of the last word's index that we find
    last, ans := 0, 0
    for i := range text {
	    // check if the first word matches the second word
		// we make use of the `last` pointer here to search for the next matching words
        if text[last:i+1] == text[n-i:n-last+1] {
			// update answer and move last pointer
            ans++
            last = i+1
        }
    }

    return ans
}
