class Solution {
public:
    int longestDecomposition(string text) {
        int left_bound=0, right_bound=text.size()-1, left=0, right=text.size()-1, left_itr, right_itr, count=0;

        while(left<=right) {
            right_itr=right;
            // right_itr will find a matched char same as at left_bound
            while(right_itr>=0 and text[right_itr]!=text[left_bound]) 
                right_itr--;
            right=right_itr;      // now we consider right window as [right, right_bound]
            left_itr=left_bound;
            // to check if we find a matched window in the left side same as right window
            while(right_itr<=right_bound and text[right_itr]==text[left_itr]) {
                right_itr++;
                left_itr++;
            }

            right_itr--;
            left_itr--;
            left=left_itr;
            // if both left window and right window matched
            if(right_itr==right_bound) {
                if(left_itr==right_itr) count+=1;      // both are actually same window
                else count+=2;                         // different window
                left_bound=left+1;
                left=left+1;
                right_bound=right-1;
                right=right-1;
            } else {
                right--;         // left window and right window didn't matched
            }

        }

        return text.size()<2 ? text.size() : count;
    }
};
