class Solution {
    public int longestDecomposition(String text) {
        int n = text.length();
        int i=0,j=text.length()-1,ans = 0;
        StringBuilder s = new StringBuilder();
        while(i<=j){
            //find the start char from last and add it in string;
            while(text.charAt(j)!=text.charAt(i)){ 
                s.append(text.charAt(j)); 
                j--;
            }
            // when both char are same we terminate from while loop so add explicitly
            s.append(text.charAt(j));

            //check string from start is equal to string from back
            if(text.substring(i,n-j).equals(s.reverse().toString())){
                ans++;
                i=n-j; 
                s.setLength(0);
