#define ll long
class Solution {
public:
    // 1-indexed string needed
    struct Hashs
    {
        vector<ll> hash;
        vector<ll> pows;
        ll P;
        ll mod;

        Hashs() {}

        Hashs(string &s,ll P,ll M) : P(P), mod(M)
        {
            ll n=s.size();
            pows.resize(n+1, 0);
            hash.resize(n+1, 0);
            pows[0] = 1;
            for(ll i=1;i<=n;i++)
            {
                pows[i]=(pows[i-1]*P)%mod;
                hash[i]=(hash[i-1]*P+(s[i-1]-'a'+1))%mod;
            }
        }

        ll get_hash(ll l,ll r)  // [l,r]
        {
            return ( hash[r] - (hash[l-1]*pows[r-l+1])%mod + mod ) % mod;
        }
    };

    int longestDecomposition(string s) 
    {
        Hashs hs(s,31,1e9+7);
        int l=1,i=1,r=s.length(),j=s.length();
        int ans=0;
        while(i<=r && j>=l)
        {
            if(hs.get_hash(l,i)==hs.get_hash(j,r))
            {
                if(j==l) ans++;
                else ans+=2;
                l=i+1;
                r=j-1;
            }
            i++; j--;
        }
        return ans;
    }
};
