class Solution {
    public int dayOfYear(String date) {
        // Days sum are som of days from the start until current month
        int[] daysSum = {0, 31, 59, 90, 120, 151, 181, 212, 243, 273, 304, 334};

        char[] digits = date.toCharArray();

        // 53328 is for (48 * 1K + 48 * 100 + 48 * 10 + 48 * 1) to convert the value to decimal from ascii value
        int year = digits[0] * 1000 + digits[1] * 100 + digits[2] * 10 + digits[3] - 53328;

        // 529 is for (48 * 10 + 48 * 1 + 1 (because array index start from 0)) to convert the value to decimal from ascii value
        int month = digits[5] * 10 + digits[6] - 529;

        int extraDayForLeap = 0;
        if (month > 1) extraDayForLeap = (year % 400 == 0) || (year % 100 == 0) || (year % 4 == 0) ? 1 : 0;

        // 529 is for (48 * 10 + 48 * 1 + 1) to convert the value to decimal from ascii value
        return digits[8] * 10 + digits[9] - 528 + daysSum[month] + extraDayForLeap;
    }
}
