impl Solution {
    pub fn day_of_year(date: String) -> i32 {
        let date = date.as_bytes();
        let day = 10 * (date[8] as i32 - 48) + date[9] as i32 - 48;
        let midx = 10 * (date[5] as usize - 48) + date[6] as usize - 48;
