      int numberOfDays = 0;// what we'll return

      int year = Integer.parseInt(date.substring(0, 4));
      int month = Integer.parseInt(date.substring(5, 7));

      // accumulate number of days in whole months prior to month containing date
      for (int m = 1; m < month; ++m) {
        numberOfDays += numDaysInMonth(m, year);
      }

      // add number of days in month containing date
      return numberOfDays + Integer.parseInt(date.substring(8, 10));
    }

    private int numDaysInMonth (int month, int year) {
      switch (month) {
        case 1:  return 31;
        //case 2:  return 28;// we'll deal with February in the default case below
        case 3:  return 31;
        case 4:  return 30;
        case 5:  return 31;
