  int dayOfYear(string date) {
        char s[date.length()+1];
        strcpy(s,date.c_str());
        char *token  =strtok(s,"-");
        int year,month,day;
        int count = 0;
        while(token!=NULL)
        {
             int x = stoi(token);

            if(count==0)
                year = x;
            else if(count==1)
                month = x;
