        int[] days_before_month = {0,31,59,90,120,151,181,212,243,273,304,334};

		// Adds extra day if it is a leap year and month is after February, then returns result.
        return((((year%4==0 && year%100!=0) || year%400==0)&&month>2)?(days_before_month[month-1]+day+1):(days_before_month[month-1]+day));
    }
