class Solution {
    public int dayOfYear(String date) {
        // Array to store the number of days in each month (assuming a non-leap year)
        int[] dayInMonths = {31, 28, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31};

        // Split the input date string into parts (year, month, and day) using "-"
        String[] parts = date.split("-");
        int year = Integer.parseInt(parts[0]);
        int month = Integer.parseInt(parts[1]);
        int day = Integer.parseInt(parts[2]);

        // Check if the given year is a leap year (divisible by 4 and not divisible by 100, or divisible by 400)
        boolean isLeapYear = (year % 4 == 0 && year % 100 != 0) || (year % 400 == 0);

        // If it's a leap year, update the number of days in February to 29
        if (isLeapYear) {
            dayInMonths[1] = 29;
        }

        // Initialize a variable to keep track of the day of the year
        int dayOfYear = 0;

        // Calculate the day of the year by summing up the days in the preceding months
        for (int i = 0; i < month - 1; i++) {
            dayOfYear += dayInMonths[i];
        }

        // Add the day of the given month to get the final day of the year
        dayOfYear += day;

        // Return the calculated day of the year
        return dayOfYear;
    }
}

