class Solution {
    public List<Boolean> canMakePaliQueries(String s, int[][] queries) {
        List<Boolean> list = new ArrayList<>();
        for (int[] q : queries) {
// if k >= 13, we can always make the substring to palindrome
            if (q[2] >= 13) {
                list.add(true);
                continue;
            }
            String str = s.substring(q[0], q[1] + 1);
            list.add(check(str, q[2]));
        }
        return list;
    }
    boolean check(String str, int k) {
        int l = 0;
        int r = str.length() - 1;
        int count = 0;
        char[] memo = new char[26];
        for (char c : str.toCharArray()) {
            memo[c - 'a']++;
        }
        for (int i = 0; i < 26; i++) {
            if (memo[i] % 2 == 1) {
                count++;
            }
        }
        if (k == 0 && count > 1) {
            return false;
        }
        if (count % 2 == 1) {
            return (count - 1) / 2 <= k;
        } else {
            return count / 2 <= k;
        }
    }
}
