    int count(int num){
        int cnt = 0;
        for(int i = 0; i < 31; i++) {
            if(num&(1<<i)) cnt++;
        }
        return cnt;
    }

    vector<bool> canMakePaliQueries(string s, vector<vector<int>>& queries) {
        vector<int> masks; //MASK WILL HELP US KEEP TRACK OF NUMBER OF ODD ELEMENTS
        int curr = 0;
        masks.push_back(0); //INITIAL MASK IS ZERO SINCE THE NUMBER OF ODD ELEMENTS IS ZERO
        for(auto x: s){
            curr^=(1<<(x - 'a')); //XOR WILL KEEP ON SWITCHING THE BITS (1->ODD, 0->EVEN)
            masks.push_back(curr); //STORING THE MASK FOR EACH INSTANCE
        }
        vector<bool> res;
        for(auto x: queries){
            int new_mask = masks[x[0]]^masks[x[1]+1]; //THE MASK WILL HOLD THE NUMBER OF ODD ELEMTS BETWEEN THE GIVEN RANGE, DRY RUN IT ONCE TO UNDERSTAND
            int cnt = count(new_mask);
			/*
			SINCE WIL HAVE NO PROBLEM IF THERE IS ONLY ONE ODD ELEMENT LIKE

			AAAAB WE CAN CONVERT IT TO AABAA
			SO WE SUBTACT COUNT BY ONE

			EVERY ELEMENT WE ARE CONVERTING WE CAN CONVERT IT INTO THE OTHER ODD OCCURING ELEMENT SO BY ONE CONVERSION WE CAN TAKE CARE OF TWO ODD NUMBERS 
			SUPPOSE WE HAVE 
			ABCD 
			AND THE NUMBER OF CONVERSION IS TWO WE CAN CONVERT IT TO
			ABBA
			SO EVEN WITH 4 ODD OCCURENCES WE WERE ABLE TO FIX IT WITH TWO CONVERSION SO WE ARE MULTIPLYING X[2] WITH 2
			*/
            res.push_back(cnt-1<= x[2]*2); 
        }
        return res;
    }
};
