```Java
  private int[][] table;

  public List<Boolean> canMakePaliQueries(String s, int[][] queries) {

    int len = s.length();
    table = new int[len][26];
    for (int i = 0; i < len; i++) {
      table[i] = i == 0 ? new int[26] : table[i - 1].clone();
      table[i][s.charAt(i) - 'a']++;
    }
    List<Boolean> res = new ArrayList<>();
    for (int i = 0; i < queries.length; i++) {
      res.add(f(queries[i]));
    }
    return res;
  }

  private boolean f(int[] arr) {
    int left = arr[0];
    int right = arr[1];
    int k = arr[2];
    if (left == right) {
      return true;
    }
    int odd = 0;
    int[] tableRight = table[right];
    if (left == 0) {
      for (int i = 0; i < 26; i++) {
        if (tableRight[i] % 2 == 1) {
          odd++;
        }
      }
    } else {
      int[] tableLeft = table[left - 1];
      for (int i = 0; i < 26; i++) {
        if ((tableRight[i] - tableLeft[i]) % 2 == 1) {
          odd++;
        }
      }
    }
    return (odd / 2) <= k;
  }
