class Solution {
    // Space: O(N*26)
    // Time: O(N)
public:
    vector<bool> canMakePaliQueries(string s, vector<vector<int>>& queries) {
        std::vector<bool>result;
        // Prefix sum algorthim 
        // Prefix[i]: An array which has size 26
        // Prefix[i][0] frequency of char 'a' in substring [0:i] 
        // Prefix[i][1] frequency of char 'b' in substring [0:i]
        std::vector<std::vector<int>>prefix((int)s.length(),std::vector<int>(26,0));
        prefix[0][s[0]-'a']=1;
        for(int i=1;i<(int)s.length();i++){
            prefix[i] = prefix[i-1];
            prefix[i][s[i]-'a']++;
        }
        for(int i=0;i<(int)queries.size();i++){
            int left = queries[i][0];
            int right = queries[i][1];
            int k = queries[i][2];
            std::vector<int>arr = prefix[right];
            // Find the frequency for each char in substring[left:right]
            if(left-1>=0)
                for(int i=0;i<(int)prefix[left-1].size();i++)
                    arr[i] -= prefix[left-1][i];
            result.push_back(is_valid(arr,k));
        }
        return result;
    }
    // To check if there is possible to rearrange string and make it palindrome
    // Only need to check the char that has odd frequency
    // For char that has even frequency, you alway can rearrange it to make it palindrome.
	// e.g "axxaxaxxxxxa" => "aa{xxxxxxxx}aa"
    // by putting half of 'a' in the front of string and another half of 'a' in the tail of the string
    bool is_valid(const std::vector<int>&arr,const int&k){
        // counter: how many char we need to replace. 
        int counter = 0;
        for(int i=0;i<(int)arr.size();i++){
            if(arr[i]%2==0) continue;
            // If a char has odd frequency, just need to one time operation to make it palindrome
            // e.g {aaaaa} =>{aaaax} just need to change one of 'a' to other char. 
            // Then frequency of 'a' becomes even.
            counter++;
        }
        // If counter is a even number, we only need to flip k/2 times
        // e.g : {abcdef} => {[abc] [cba]} => total 3 times
        // If counter is a odd number, we only need to flip (k-1)/2 times
        // e.g {abcde} => {[ab] c [ba]} => total 2 times
        return (counter%2==0&&counter/2<=k) || (counter%2!=0 && (counter-1)/2<=k);
    }
};
