    vector<bool> canMakePaliQueries(string s, vector<vector<int>>& queries) {
        int n=s.size();
		//prefix array to allow me to compute the xor of a substring in constant time
        vector<int>prefix(n+1,0);
        for(int i=1;i<prefix.size();i++){
