class Solution {
    public List<Boolean> canMakePaliQueries(String s, int[][] queries) {
        int[][] dp = new int[s.length() + 1][26]; // Frequency of all the characters for substring from 1 to len

        for(int i=1; i<=s.length(); i++){
            for(int j=0; j<26; j++){
                if(s.charAt(i-1) - 'a' == j){
                    dp[i][j] = dp[i-1][j] + 1;
                }else dp[i][j] = dp[i-1][j];
            }
        }

        List<Boolean> res = new ArrayList<>();
        for(int[] query : queries){
            int q0 = query[0];
            int q1 = query[1];
            int k = query[2];

            int[] leftCount = dp[q0];
            int[] rightCount = dp[q1+1];

            int diffChar = 0; // Count the unmatched 
            for(int i=0; i<26; i++){
                diffChar += (rightCount[i] - leftCount[i])%2;
            }

            Boolean possibleOrNot = false;
            if( (q1 - q0 + 1)%2 != 0){ // Odd Length
                diffChar -= 1; // One char at the middle 
            }

            if(diffChar/2 <= k) possibleOrNot = true;
            res.add(possibleOrNot);
        }

        return res;
    }
}
