def canMakePaliQueries(self, s: str, queries: List[List[int]]) -> List[bool]:    
	prefix = {}
	l = len(s)
	prefix[0] = {s[0]:1}

	for i in range(1, l):
		prefix[i] = prefix[i-1].copy()
		if s[i] in prefix[i]:
			prefix[i][s[i]]+=1
		else:
			prefix[i][s[i]] = 1
	#print(prefix)

	res = []

	for q in queries:
		one = 0

		for k in prefix[q[1]]:
			chars = prefix[q[1]][k]-(prefix[q[0]-1].get(k, 0) if q[0]>0 else 0)
			one+=chars%2
		if one<=1:
			res.append(True)
		else:
			res.append(q[2]>=(one//2))
	return res
