class Solution {
public:
    vector<bool> canMakePaliQueries(string s, vector<vector<int>>& queries) {
        // Create bitmask to keep track of whether each character count is odd
        vector<int> charBitmask(s.size() + 1, 0);
        for (int i = 0; i < s.size(); i++)
            charBitmask[i + 1] = charBitmask[i] ^ (1 << (s[i] - 'a')); // Toggle bit representing the character

        vector<bool> ret;
        for (int i = 0; i < queries.size(); i++) {

            int oddBitmask = charBitmask[queries[i][1] + 1] ^ charBitmask[queries[i][0]]; // Find differences in bitmasks
            int countOdd = 0;
            while (oddBitmask) {
                oddBitmask -= oddBitmask & -oddBitmask; // Count number of bits in bitmask
                countOdd++;
            }
            ret.push_back(countOdd <= queries[i][2] * 2 + 1); // If number of bits is more than 2 * k + 1, it can't be palindrome
        }
        return ret;
    }
};
