class Solution {
public:
    vector<bool> canMakePaliQueries(string s, vector<vector<int>>& queries) {
        int n=s.size();
        vector<vector<int>>dp(n,vector<int>(26,0));
        for(int i=0;i<s.size();i++){
            int j=s[i]-'a';
            dp[i][j]+=1;
        }
        for(int i=1;i<s.size();i++){
            for(int j=0;j<26;j++){
                dp[i][j]+=dp[i-1][j];
            }
        }
        vector<bool>ans;
        for(auto vec:queries){
            int l=vec[0];
            int r=vec[1];
            int s=vec[2];
            int odd=0;
            for(int j=0;j<26;j++){
                int count=dp[r][j]-(l==0?0:dp[l-1][j]);
                if(count%2==1) odd++;
            }
            if(odd==0 || odd==1){
                ans.push_back(true);
                continue;
            }
            int match=odd/2;
            if(match<=s){
                ans.push_back(true);
            }
            else{
                ans.push_back(false);
            }
        }
        return ans;
    }
};
