var canMakePaliQueries = function(s, queries) {
    const len = s.length
    const mapSF = new Array(len)
    mapSF[-1] = new Map()

    for (let i = 0; i < len; i++) {
        const ch = s[i]

        let prevOutcome = mapSF[-1 + i]
        let outcome = new Map(prevOutcome)
        outcome.set(ch, 1 + (outcome.get(ch) || 0))

        mapSF[i] = outcome
    }


    let result = queries.map(query => {
        const [fromIndex, toIndex, k] = query
        const startMap = mapSF[-1 + fromIndex]
        const endMap = mapSF[toIndex]

        let oddCount = 0
        for (const [ch, endCount] of endMap.entries()) {
            const startCount = startMap.get(ch) || 0

            const diff = endCount - startCount
            if (diff & 1) {
                oddCount++
            }
        }

        if (oddCount > 2 * k + 1)
            return false
        else
            return true
    })


    return result
};
