    vector<bool> canMakePaliQueries(string s, vector<vector<int>>& queries) {
        preprocess(s);
        std::vector<bool> answer;
        for (const auto& query : queries) {
            const auto oddNum = std::bitset<26>(prefix[query[1] + 1] ^ prefix[query[0]]).count();
            answer.emplace_back(oddNum / 2 <= query[2]);
        }
        return answer;
    }

private:
    void preprocess(const string& s) {
        prefix = std::vector<unsigned>(s.size() + 1, 0);
        for (std::size_t i = 1; i <= s.size(); ++i) {
            prefix[i] = prefix[i - 1] ^ (1u << (s[i - 1] - 'a'));
        }
    }

    std::vector<unsigned> prefix;
};
