class Solution {
public:
    vector<bool> canMakePaliQueries(string s, vector<vector<int>>& queries) {
        vector<bool> res;
        vector<bitset<26>> bitmap(s.size()+1);
        bitset<26> bit;

        for(int i=0; i<s.size(); i++){
            bit.flip(s[i] - 'a');
            bitmap[i+1] = bit;
        }

        for(int i=0; i<queries.size();i++){
            if(queries[i][2] >= 13){
                res.push_back(true);
                continue;
            }
            int begin = queries[i][0], end = queries[i][1];
            auto tmp = bitmap[begin] ^ bitmap[end+1];
            if(tmp.count()/2 <= queries[i][2]){
                res.push_back(true);
                continue;
            }
            res.push_back(false);
        }
        return res;
    }
};
