class Solution 
{
public:
    inline const vector<bool> canMakePaliQueries(
        const string & s, 
        const vector<vector<int>>& queries) const 
    {
        // define a bitset with the allowed chars range (26)
        using CharBitset = bitset<26>;

        // define a vector to count the pairty of the chars
        vector<CharBitset> pairtyVec;

        // reserve memory to lower the amount of allocations
        // notice the "+ 1" - as we need one extra for the inital state
        pairtyVec.reserve(s.size() + 1);

        // this is the bitset we will update in the loop
        CharBitset curBitSet;

        // add an inital "all set to false" bitset as our start
        pairtyVec.push_back(curBitSet);

        // loop the chars
        for(const auto c : s)            
        {
            // flip the current pairty
            curBitSet[ c - 'a' ].flip();

            // push back to our vector
            pairtyVec.push_back(curBitSet);
        }

        // create res vec
        vector<bool> res;

        // reserve to save allocations
        res.reserve(queries.size());

        // loop the queries
        for(const auto & q : queries)
        {
            // get the start bitset
            const auto & startBs = pairtyVec[ q[0] ];

            // get the end bitset - notice the "+1"
            const auto & endBs = pairtyVec[ q[1] + 1 ];

            // xor the bitsets, the bits that remain on are the odds
            const auto diffBs = startBs ^ endBs;

            // use count() to get the "on" bits
            const auto oddsCount = diffBs.count();

            // this is the tricky part, let me explain
            // lets say we have 5 odds "abcde"
            // by replacing one of them to a diffrent one
            // we get rid of them both, for example if we change 'a'
            // to 'e' - then both of their count are even.
            // as we dvide the odd count that is an integer by 2
            // if the odd count is odd (for example 1) this is
            // ignored, as it should as we are allowed one odd
            // char count            
            const auto curRes = q[2] >= oddsCount / 2;

            // add to the result
            res.push_back(curRes);
        }

        // return the result
        return res;

    }
};
