class Solution {
    public List<Boolean> canMakePaliQueries(String s, int[][] queries) {
        int len = s.length();
        int[] bits = new int[len];
        int his = 0;
        for (int i = 0; i < len; i++) {
            his = his ^ (1 << (s.charAt(i) - 'a'));
            bits[i] = his;
        }
        List<Boolean> result = new ArrayList<>();
        for (int i = 0; i < queries.length; i++) {
            int[] query = queries[i];
            int tmp = (query[0] == 0 ? 0 : bits[query[0] - 1]) ^ bits[query[1]];//when index=0, the previous one is 0
            int odd = 0;
            while (tmp != 0) {
                odd += tmp & 1;
                tmp >>>= 1;
            }
            result.add(query[2] >= odd / 2);
        }
        return result;
    }
}
