    public List<Boolean> canMakePaliQueries(String s, int[][] queries) {
        List<Boolean> ret = new ArrayList<>();

        int[][] freqDp = new int[s.length()][26];

        int[] freq = new int[26];

        for (int i = 0; i < s.length(); i++) {
            freq[s.charAt(i) - 'a']++;
            freqDp[i] = freq.clone();
        }

        for (int[] q : queries) {
            ret.add(canMake(s, q[0], q[1], q[2], freqDp));
        }

        return ret;
    }

    private boolean canMake(String s, int left, int right, int k, int[][] freqDp) {
        if (right - left + 1 == 1) return true;

        if (right - left + 1 <= k) return true;

        int[] freq = new int[26];

        for (int i = 0; i < 26; i++) {
            freq[i] = freqDp[right][i];
            if (left-1 >= 0)
                freq[i] -= freqDp[left-1][i];
        }

        int oddCount = 0;

        for (int n : freq) {
            if (n % 2 == 1) 
                oddCount++;
        }

        if ((right - left + 1) % 2 == 1) oddCount -= 1;

        return k*2 >= oddCount;
    }
