class Solution {
    public List<Boolean> canMakePaliQueries(String s, int[][] queries) {
        if (queries == null || queries.length == 0) {
            return new ArrayList<>();
        }
        List<Boolean> res = new ArrayList<>();
        Map<Integer,int[]> map = new HashMap<>();
        map.put(-1, new int[26]);
        char[] chs = s.toCharArray();
        int[] chars = new int[26];
        for (int j = 0; j < chs.length; j++) {
            chars[chs[j] - 'a']++;
            map.put(j, Arrays.copyOfRange(chars, 0, 26));
        }
        for (int[] inte : queries) {
            if (inte[0] == inte[1]) {
                res.add(true);
                continue;
            }
            int[] before = map.get(inte[0] - 1);
            int[] after = map.get(inte[1]);
            int count = 0;
            for (int i = 0; i < 26; i++) {
                if ((after[i] - before[i]) % 2 != 0) {
                    count++;
                }
            }
            res.add(count / 2 <= inte[2] ? true : false);
        }
        return res;
    }
}
