class Solution {
    public List<Boolean> canMakePaliQueries(String s, int[][] queries) {
        List<Boolean> ans = new ArrayList<>();
        int n = s.length();
        int[][] dataSet = new int[n][26];
        for(int i = 0 ; i < n ; i++){
            char ele = s.charAt(i);
            if(i > 0){
                for(int j = 0 ; j < 25 ; j++){
                    dataSet[i][j] = dataSet[i-1][j];
                }
            }
            dataSet[i][ele - 'a']++;
        }

        for(int[] q : queries){
            int start = q[0];
            int end = q[1];
            int k = q[2];
            int valid = 0;
            int inValid = 0;
            int len = end - start + 1;
            for(int j = 0 ; j < 25 ; j++){
                int count = dataSet[end][j];
                if(start - 1 >= 0){
                    count -= dataSet[start-1][j];
                }
                if(count > 0 && count % 2 == 0){
                    valid++;
                }else if(count > 0){
                    inValid++;
                }
            }
            if(len % 2 != 0 && inValid > 0) {
                inValid--;
            }
            if(k > 0 && k * 2 >= inValid) 
                ans.add(true);
            else if(k == 0 && inValid == 0)
                ans.add(true);
            else
                ans.add(false);
        }
        return ans;
    }
}
