class Solution {
public:
    vector<bool> canMakePaliQueries(string s, vector<vector<int>>& queries) {
        vector<bool>ans;
        int n=s.size();
        vector<int>pre(n,0);
        pre[0]=1<<(s[0]-'a');
        for(int i=1;i<n;i++){
            //left shift each char position and take xor        
            pre[i]=pre[i-1]^(1<<(s[i]-'a'));
        } 
        for(int i=0;i<queries.size();i++){
            int k=queries[i][2];
            int l=queries[i][0];
            int r=queries[i][1];
            int x;
            if(l==0){
                x=pre[r];
            }else if(l>0){
                //xor till l-1 (xor) xor till r gives the xor bw l r
                x=pre[l-1]^pre[r];
            }
            int st=__builtin_popcount(x);
            //counted the set bits in xored product to determine
            //non repeating chars
            st/=2;     
            // divide the non repeating chars frequency by 2 as any 2 different
            // chars can be replaced by a single char to make them equal...also 
            // /2 ensures removal of odd frequency elements like dbc st=3 (set
            // bits) as d b c are 3 single non repeating ..when /2 it becomes 1 
            // making d and c equal and palindrome can be dbd/ cbc (b remaining unaffected)
            if(st<=k){
                ans.push_back(1);
            }else{
                ans.push_back(0);
            }
        }
        return ans;
    }
};
```
