class Solution:
    def canMakePaliQueries(self, s: str, queries: List[List[int]]) -> List[bool]:
        masks = [0]
        for i, c in enumerate(s):
            masks.append((1 << (ord(s[i]) - ord('a'))) ^ masks[-1])

        ans = []
        for (l, r, k) in queries:
            ans.append(bin(masks[r + 1] ^ masks[l])[2:].count('1') <= 2 * k + 1)
        return ans
