class Solution {
public:
    vector<bool> canMakePaliQueries(string s, vector<vector<int>>& queries) {
        int n=s.size();
        int qz=queries.size();
        vector<int> freq(n,0); 
        //stores whether freq of all the characters till i are either odd or even

        int temp=0;
        for(int i=0;i<n;i++){
            temp=temp^(1<<(s[i]-'a'));  //makes 0 to 1 and 1 to 0
            freq[i]=temp;
        }
        vector<bool>ans(qz,0);
        for(int i=0;i<qz;i++){
            int from = queries[i][0],to=queries[i][1],k=queries[i][2];
            int temp=freq[to];
            if(from>0) temp=temp^freq[from-1];
            int count=0;    //odd counts
            while(temp!=0){
                count+=(temp&1);
                temp/=2;
            }
            //we can make max k character to get into palindrome
            ans[i]=(count/2)<=k;
        }
        return ans;
    }
};
