class Solution {

    static final int PRE_ARRAY_LENGTH = 26;
    public List<Boolean> canMakePaliQueries(String s, int[][] queries) {

        List<Boolean> result = new ArrayList<>();
        //If it is an even number then it must form a palindrome
        //If there is an odd number then a classification is required:
        //If there is an odd number, then no exchange is required
        //2 for 1 exchange
        //3 for 1
        //4 for 2
        //5 for 2

        int[][] preCharArray = new int [s.length()+1][PRE_ARRAY_LENGTH];
        // Constructing prefix arrays
        buildPreCharacterArray(preCharArray,s);
        // traversing queries
        for (int[] query : queries) {
            int left = query[0];
            int right = query[1];
            int k = query[2];
            //If k is greater than or equal to the number of odd numbers, then the palindrome must be formed
            //first even characters - even characters must be even characters do not affect
            if (judgeIsPalindrome(preCharArray[right+1],preCharArray[left],k)){
                result.add(true);
            }else {
                result.add(false);
            }
        }
        return result;
    }

    private void buildPreCharacterArray(int [][] preCharArray,String s){
        for(int i=1;i<=s.length();i++){
            for(int j=0;j<PRE_ARRAY_LENGTH;j++){
                preCharArray[i][j] = preCharArray[i-1][j];
            }
            preCharArray[i][s.charAt(i-1)-'a']++;
        }
    }

