class Solution:
    def canMakePaliQueries(self, s: str, queries: List[List[int]]) -> List[bool]:
        prefix = [0]
        for le in s: # prefix sum to check which letters whose number is odd
            prefix.append(prefix[-1] ^ 1 << (ord(le) - ord('a')))
        ans = []
        for l, r, k in queries:
            odd = (prefix[r+1] ^ prefix[l]).bit_count() # number of letters whose number is odd
            if k >= odd//2: ans.append(True)
            else: ans.append(False)
        return ans
