class Solution {
public:
    vector<bool> canMakePaliQueries(string s, vector<vector<int>>& q) {
        vector<bool> ans;
        int n = s.length();
        vector<vector<int>> v(n,vector<int>(26,0));
        v[0][s[0]-'a']++;
        for(int i=1; i<n; i++){
            v[i] = v[i-1];
            v[i][s[i]-'a']++;
        }
        for(int i=0; i<q.size(); i++){
            int left = q[i][0];
            int right = q[i][1];
            int k = q[i][2];
            int len = right-left+1;
            int cnt = len%2==0 ? 0 : -1;
            for(int j=0; j<26; j++){
                int top = v[right][j];
                int bottom = left==0 ? 0 : v[left-1][j];
                int diff = top-bottom;
                if(diff%2==1)cnt++;
            }
            ans.push_back(cnt/2<=k);
        }
        return ans;
    }
};
