class Solution {
    int dp[][];

    void preparePallindrome(String s){
        dp = new int[s.length()+1][26];

        // prepare quick lookup matrix similar to prefix sum, this will give us how many char are there between (i,j)
        for(int i=0; i<s.length(); i++){

            dp[i+1][s.charAt(i)- 'a']++;

            for(int j=0; j<26;j++){
                dp[i+1][j]+=dp[i][j];
            }    
        }

    }
    public List<Boolean> canMakePaliQueries(String s, int[][] queries) {

        preparePallindrome(s);    
        List<Boolean> ans = new ArrayList<>();
        for(int i=0; i<queries.length; i++){
            int left = queries[i][0];
            int right = queries[i][1];
            int changes = queries[i][2];

            // if no of operations is greater than number of character in substring then we can convert all character in single character
            if(right - left+1 <=changes) ans.add(true);
            else {
                int odd=0;

                for(int j=0; j<26; j++){
                    // right+1 as we've to include right
                    int c = dp[right+1][j] - dp[left][j];
                    if(c%2!=0) odd++;
                }
                //convert two odd into same and make a pair, so we can make max 2*operations odd to pallindrome
                odd = odd - 2*changes;
                if(odd>1) ans.add(false);
                else ans.add(true);
            }
        }
        return ans;
    }
}
