var canMakePaliQueries = function(s, queries) {
    const DEFAULT_CHAR_CODE = 'a'.charCodeAt(0);
    const size = s.length;
    const prefixSum = Array(size + 1);

    prefixSum[0] = Array(26).fill(0);

    for (let index = 1; index <= size; index++) {
        const str = s[index - 1];
        const charCode = str.charCodeAt(0) - DEFAULT_CHAR_CODE;

        prefixSum[index] = [...prefixSum[index - 1]];
        prefixSum[index][charCode] += 1;
    }

    return queries.map(([left, right, k]) => {
        let sum = 0;

        for (let code = 0; code < 26; code++) {
            sum += (prefixSum[right + 1][code] - prefixSum[left][code]) % 2;
        }
        return (sum / 2 | 0) <= k;
    });
};
