class Solution {
public:
    pair<int,int> getDifference(vector<int>&a , vector<int> &b){
        int odd = 0 , even = 0;

        for(int i=0 ; i<26 ; i++){
            int d = b[i]-a[i];
            if(d%2==0 and d!=0) even++;
            else if(d%2==1)odd++;
        }

        return {odd , even};
    }
    vector<bool> canMakePaliQueries(string s, vector<vector<int>>& queries) {
        int n = s.size();
        int m = queries.size();
        vector<bool> ans(m , false);

        vector<vector<int>> freq(n, vector<int>(26 , 0));

        vector<int> mp(26 , 0);

        for(int i=0 ; i<n ; i++){
            mp[s[i]-'a']++;
            freq[i] = mp;
        }

        for(int i=0 ; i<m ; i++){
            int l = queries[i][0];
            int r = queries[i][1];
            int k = queries[i][2];

            pair<int,int> p;

            if(l==0){
                vector<int> temp(26 , 0);
                p = getDifference(temp , freq[r]);
            }
            else{
                p = getDifference(freq[l-1] , freq[r]);
            }

            int d = r-l+1;

            if(d%2==0){
                if(p.first<=2*k) ans[i] = true;
            }
            else{
                if(p.first==1){
                    ans[i] = true;
                }

                int x = p.first-1;

                if(x%2==0 and x<=2*k){
                    ans[i] = true;
                }
                else{
                    k--;
                    if(x<=2*k){
                        ans[i] = true;
                    }
                }


            }

        }


        return ans;
    }
};
```

