class Solution:

    def using_prefix(self, s, queires):
        m = len(s)
        n = len(queries)
        self.res = [False] * n
        prefix = [[0]*26 for _ in range(m+1)]

        for i in range(m):
            prefix[i+1] = prefix[i][:]
            prefix[i+1][ord(s[i])-ord('a')] += 1

        for j in range(n):
            l, r, k = queries[j]

            if r-l+1 <= k:
                self.res[j] = True
                continue
            k *= 2
            middle = (r-l+1)%2 != 0

            for i in range(26):

                if k < -1:
                    break
                elif k == -1 and not middle:
                    break 
                substring = prefix[r+1][i] - prefix[l][i]

                if substring %2 != 0:
                    k -= 1
            # print(j, k)
            if k >=0 or (k ==-1 and middle):
                self.res[j] = True

        return self.res
    def canMakePaliQueries(self, s: str, queries: List[List[int]]) -> List[bool]:

        # use 26 * m space prefix
        # return self.using_prefix(s, queries)
        m, n = len(s), len(queries)
        prefix = [0]* (m+1)
        prefix[0] = 1<<26
        res = [False]*n
        for i in range(m):
            prefix[i+1] = prefix[i]^(1<<(ord(s[i])-ord('a')))

        for j in range(n):
            l, r, k = queries[j]
            odd = bin(prefix[r+1]^prefix[l]).count('1') -1

            if odd - ((r+1-l)%2 !=0) <= 2*k:
                res[j] =True

        return res






