class Solution {
public:
    vector<bool> canMakePaliQueries(string s, vector<vector<int>>& queries) {
        int n = s.length();
        vector<long long> prefix(n+1, 0);
        vector<bool> ans(queries.size(), false);
        long long mask = 1;
        prefix[1] = (mask << (s[0]-'a'));
        for(int i = 1 ; i < n ; i++)
            prefix[i+1] = (prefix[i] ^ (mask << s[i]-'a'));

        for(int i = 0 ; i < queries.size() ; i++){
            int l = queries[i][0];
            int r = queries[i][1];
            int k = queries[i][2];

            long long val = (prefix[r+1]^prefix[l]);

            int setBits = 0;
            while(val != 0){
                int rmsb = (val & -val);
                val = val - rmsb;
                setBits++;
            }

            if(setBits == 0 || setBits == 1)    ans[i] = true;
            else if(setBits-2*k <= 1)   ans[i] = true;
        } 

        return ans;
    }
};
