func canMakePaliQueries(s string, queries [][]int) []bool {
  psum := make([][26]int, len(s)+1)
  for i := 0; i < len(s); i++ {
    psum[i+1] = psum[i]
    psum[i+1][s[i]-'a']++
  }
  res := make([]bool, len(queries))
  for idx, query := range queries {
    left, right, k := query[0], query[1], query[2]
    var odds int
    for i := 0; i < 26; i++ {
      if (psum[right+1][i] - psum[left][i])%2 == 1 {
        odds++
      }
    }
    odds -= k*2 // The corrections we can make
    res[idx] = odds <= 1 // There must be at most 1 odd after the corrections
  }
  return res
}
