class Solution {
public:
    vector<bool> canMakePaliQueries(const string& s, const vector<vector<int>>& queries) {
        vector<int> masks(s.size());
        for(size_t i=0; i<s.size(); ++i)
            masks[i]=(i? masks[i-1]: 0) ^ (1<<s[i]-'a');

        vector<bool> res(queries.size());
        for(size_t i=0; i<queries.size(); ++i) {
            int odd=bitset<32>((queries[i][0]? masks[queries[i][0]-1]: 0) ^ masks[queries[i][1]]).count();
            if((odd%2? --odd: odd)/2 <= queries[i][2]) res[i]=true;
        }

        return res;
    }
};
