class Solution {
    vector<int> f;
    string str;
public:
    int get(int i, int j) {
        int ans = 0;
        while (j>=i) {
            if(j - (j & -j) >= i) {
                ans ^= f[j];
                j -= (j & -j);
            } else {
                ans ^= (1<<(str[j-1]-'a'));
                j--;
            }
        }
        return ans;
    }

    void update(int i, char c) {
        while (i<=str.size()) {
            f[i] ^= (1 << (c-'a'));
            i += (i & -i);
        }
    }

    int countSetBit(int num) {
        return __builtin_popcount(num);
    }

    vector<bool> canMakePaliQueries(string s, vector<vector<int>>& queries) {
        vector<bool> ans = vector<bool>(queries.size(), false);
        f = vector<int>(s.length() + 1, 0);
        str = s;

        for(int i=1; i<=s.length(); i++) {
            update(i, s[i-1]);
        }

        int i = 0;  
        for(auto q: queries) {
            int left = q[0]+1, right = q[1]+1, k = q[2];
            int xorRange = get(left,right);
            int setBits = countSetBit(xorRange);
            if(setBits / 2 <= k) {
                ans[i] = true;
            }
            i++;
        }

        return ans;
    }
};
```

