class Solution:
    def canMakePaliQueries(self, s: str, queries: List[List[int]]) -> List[bool]:

        freqs = [ 1 << 26 - 1 ] # parity of the frequencies

        for w in s:
            p = ord(w) - ord('a') # position to toggle
            freqs.append(freqs[-1]^(1 << p))

        res = []

        for l,r,k in queries:
            count = (freqs[r+1]^freqs[l]).bit_count() # count odd freqs
            res.append( k >= (count // 2) )

        return res
