class Solution {
public:
    vector<bool> canMakePaliQueries(string s, vector<vector<int>>& queries) {

        int n = s.size();
        vector<vector<int>> prefix_letter_count(26, vector<int>(n, 0));
        prefix_letter_count[s[0] - 'a'][0] = 1;
        for (int i = 1; i < n; i++){
            int current_letter = s[i] - 'a';
            for (int letter = 0; letter < 26; letter++){
                prefix_letter_count[letter][i] = prefix_letter_count[letter][i - 1];
                if (letter == current_letter){
                    prefix_letter_count[letter][i]++;
                }
            }
        }
        vector<bool> result(queries.size());
        int region_letter_count[26] = {0};
        for (int i = 0; i < queries.size(); i++){
            int start = queries[i][0], back = queries[i][1], k = queries[i][2];
            int size = back - start + 1;
            if (k >= size / 2){
                result[i] = true;
                continue;
            }
            // check the [start, back] in s
            memset(region_letter_count, 0, sizeof(region_letter_count));
            if (start == 0){
                for (int letter = 0; letter < 26; letter++){
                    region_letter_count[letter] = prefix_letter_count[letter][back];
                }
            }
            else{
                for (int letter = 0; letter < 26; letter++){
                    region_letter_count[letter] = prefix_letter_count[letter][back] - prefix_letter_count[letter][start - 1];
                }
            }
            // check whether the region_letter_count is satisfied:
            size = 0;
            for (int letter = 0; letter < 26; letter++){
                if (region_letter_count[letter] % 2 == 1){
                    size++;
                }
            }
            if (k >= size / 2){result[i] = true;}
            else{result[i] = false;}
        }
        return result;
    }
};
