class Solution:
    def canMakePaliQueries(self, s: str, queries: List[List[int]]) -> List[bool]:
        freq_array = [self.convertStringToArrayFreq(s[0])]

        for i in range(1, len(s)):
            prev_freq = freq_array[i-1].copy()
            prev_freq[ord(s[i]) - 97] += 1
            freq_array.append(prev_freq)

        res = []
        for left, right, num in queries:
            if left - 1 < 0: 
                substring_freq = freq_array[right]
            else:
                substring_freq = self.getFreq(freq_array[left-1], freq_array[right])
            numOdd = self.getNumberOfOddFrequencies(substring_freq)
            res.append(numOdd//2 <= num)

        return res

    def getFreq(self, start, end):
        ar = [0] * 26
        for i in range(0, len(ar)):
            ar[i] = end[i] - start[i]
        return ar

    def convertStringToArrayFreq(self, word):
        ar = [0]*26
        for letter in word:
            ar[ord(letter) - 97] += 1
        return ar

    def getNumberOfOddFrequencies(self, count):
        res = 0
        for key, value in enumerate(count):
            res += value % 2
        return res

