class Solution {
public:
    vector<bool> canMakePaliQueries(string s, vector<vector<int>>& queries) {
        vector<vector<int>> dp;
        vector<int> v(26, 0);
        int n = s.size();
        for(int i = 0; i < n; i++){
            char ch = s[i];
            v[ch-'a']++;
            dp.push_back(v);
        }
        vector<bool> ans;
        for(auto it : queries){
            int start = it[0];
            int end = it[1];
            int k = it[2];


            int count = 0;

            for(int i = 0; i < 26; i++){
                int val = dp[end][i] - ((start > 0)?dp[start-1][i]:0);
                if(val%2 != 0) count++;
            }
            if(count/2 <= k) ans.push_back(true);
            else ans.push_back(false);
        }
        return ans;
    }
};
```

